import React, { useEffect, useState } from "react";

const TeacherDashboard = () => {
  const [students, setStudents] = useState([]);
  const [classes, setClasses] = useState([]);
  const [learningMaterials, setLearningMaterials] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [materialTitle, setMaterialTitle] = useState("");
  const [materialId, setMaterialId] = useState(null); // For PUT updates

  // Fetch all students or by class ID
  const getStudents = async (classId) => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `https://seclink-server.onrender.com/students?class_id=${classId || ""}`,
        {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const data = await response.json();
      if (response.ok) {
        setStudents(data);
      } else {
        console.error("Error fetching students:", data.message);
      }
    } catch (error) {
      console.error("Error fetching students:", error);
    }
  };

  // Fetch all classes
  const getClasses = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        "https://seclink-server.onrender.com/classes",
        {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const data = await response.json();
      if (response.ok) {
        setClasses(data);
      } else {
        console.error("Error fetching classes:", data.message);
      }
    } catch (error) {
      console.error("Error fetching classes:", error);
    }
  };

  // Fetch learning materials
  const getLearningMaterials = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        "https://seclink-server.onrender.com/learning-material",
        {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const data = await response.json();
      if (response.ok) {
        setLearningMaterials(data);
      } else {
        console.error("Error fetching learning materials:", data.message);
      }
    } catch (error) {
      console.error("Error fetching learning materials:", error);
    }
  };

  // Fetch notifications
  const getNotifications = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        "https://seclink-server.onrender.com/notifications",
        {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const data = await response.json();
      if (response.ok) {
        setNotifications(data);
      } else {
        console.error("Error fetching notifications:", data.message);
      }
    } catch (error) {
      console.error("Error fetching notifications:", error);
    }
  };

  // Upload or update learning material
  const handleLearningMaterial = async (event) => {
    event.preventDefault();
    if (!selectedFile || !materialTitle) {
      alert("Please fill in all fields.");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);
    formData.append("title", materialTitle);
    if (materialId) {
      formData.append("id", materialId); // Include material ID for updates
    }

    const requestOptions = {
      method: materialId ? "PUT" : "POST", // Use PUT for updates, POST for new uploads
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
        // 'Content-Type' header is omitted because FormData sets it automatically
      },
      body: formData,
    };

    try {
      const response = await fetch(
        "https://seclink-server.onrender.com/learning-material",
        requestOptions
      );
      const data = await response.json();
      if (response.ok) {
        console.log("Learning material saved successfully", data);
        getLearningMaterials(); // Refresh learning materials
        setMaterialId(null); // Reset for new uploads
      } else {
        console.error("Error saving learning material:", data.message);
      }
    } catch (error) {
      console.error("Error saving learning material:", error);
    }
  };

  // Handle file input change
  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  useEffect(() => {
    getStudents();
    getClasses();
    getLearningMaterials();
    getNotifications();
  }, []);

  return (
    <div>
      <h1>Teacher Dashboard</h1>

      {/* Students Section */}
      <div id="students">
        <h2>Students</h2>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Class</th>
            </tr>
          </thead>
          <tbody>
            {students.map((student) => (
              <tr key={student.id}>
                <td>{student.id}</td>
                <td>{student.name}</td>
                <td>{student.class}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Classes Section */}
      <div id="classes">
        <h2>Classes</h2>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Teacher</th>
            </tr>
          </thead>
          <tbody>
            {classes.map((classItem) => (
              <tr key={classItem.id}>
                <td>{classItem.id}</td>
                <td>{classItem.name}</td>
                <td>{classItem.teacher}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Learning Materials Section */}
      <div id="learningMaterials">
        <h2>Learning Materials</h2>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>File Path</th>
            </tr>
          </thead>
          <tbody>
            {learningMaterials.map((material) => (
              <tr key={material.id}>
                <td>{material.id}</td>
                <td>{material.title}</td>
                <td>{material.file_path}</td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Upload or Update Learning Material */}
        <h3>Upload or Update Learning Material</h3>
        <form onSubmit={handleLearningMaterial}>
          <label>
            Title:
            <input
              type="text"
              value={materialTitle}
              onChange={(e) => setMaterialTitle(e.target.value)}
              required
            />
          </label>
          <label>
            Select File:
            <input type="file" onChange={handleFileChange} required />
          </label>
          <button type="submit">{materialId ? "Update" : "Upload"}</button>
        </form>
      </div>

      {/* Notifications Section */}
      <div id="notifications">
        <h2>Notifications</h2>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Message</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {notifications.map((notification) => (
              <tr key={notification.id}>
                <td>{notification.id}</td>
                <td>{notification.message}</td>
                <td>{notification.date}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default TeacherDashboard;
