import React, { useState, useEffect } from 'react';
import ParentDashboard from './ParentDashboard';  // Adjust the path as needed
import TeacherDashboard from './TeacherDashboard';  // Adjust the path as needed
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [role, setRole] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (!token) {
      navigate('/login'); // Redirect to login if no token found
      return;
    }

    // Decoding the token to get the user's role
    const decodedToken = JSON.parse(atob(token.split('.')[1])); // Decode the JWT token
    setRole(decodedToken.role);  // Assuming role is stored in the token
  }, [navigate]);

  // If the role is not yet set, show a loading message
  if (!role) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Dashboard</h1>
      {role === 'Parent' && <ParentDashboard />}
      {role === 'Teacher' && <TeacherDashboard />}
    </div>
  );
};

export default Dashboard;
