import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

function Signup() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("teacher");
  const [signupSuccess, setSignupSuccess] = useState(false);
  const [token, setToken] = useState(''); // Keep the token state for signup if required
  const navigate = useNavigate(); // Navigate to login after successful signup

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:5555/signup", { username, password, role })
      .then((response) => {
        alert(response.data.message);
        setSignupSuccess(true);

        // Assuming the response contains a token (though it's typically given on login, not signup)
        const signupToken = response.data.token;
        if (signupToken) {
          setToken(signupToken);  // Save token in state if needed
          localStorage.setItem('token', signupToken);  // Optionally store it in localStorage
        }

        // Redirect to login page
        navigate("/login");  // Automatically redirects to login after signup
      })
      .catch((error) => {
        alert(error.response.data.message || "Error signing up");
      });
  };

  return (
    <div className="card signup-card">
      <form onSubmit={handleSubmit}>
        <h2>Signup</h2>
        <label>Username: </label>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />

        <label>Password: </label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />

        <label>Role: </label>
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option value="teacher">Teacher</option>
          <option value="parent">Parent</option>
        </select>

        <button type="submit">Sign Up</button>

        {signupSuccess && (
          <div className="success-message">
            <p>Signup successful! You can now log in:</p>
            <Link to="/login" className="btn-login">Login</Link>
          </div>
        )}
      </form>
      <p>
        Already have an account? <Link to="/login" className="btn-login">Login</Link>
      </p>
    </div>
  );
}

export default Signup;
