import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

function Signup() {
  const [username, setUsername] = useState("");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [subject, setSubject] = useState(""); // Subject is needed for Teacher role
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("Teacher"); // Default to "Teacher" with capitalized first letter
  const [loading, setLoading] = useState(false);
  const [signupSuccess, setSignupSuccess] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  // Handle signup form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErrorMessage("");

    try {
      // Create the data object to send
      const signupData = {
        name,
        username,
        email,
        password,
        role, // Send the selected role
        subject: role === "Teacher" ? subject : null, // Only send subject if role is Teacher
      };

      const response = await axios.post(
        "http://localhost:5555/signup",
        signupData
      );

      if (response.status === 201) {
        setSignupSuccess(true);
        setLoading(false);
        setTimeout(() => navigate("/login"), 2000); // Redirect to login after success
      }
    } catch (error) {
      setLoading(false);
      setErrorMessage(error.response?.data?.message || "Error signing up");
    }
  };

  return (
    <div className="card signup-card">
      <form onSubmit={handleSubmit}>
        <h2>Sign Up</h2>
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        <label>Username: </label>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <label>Email: </label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <label>Name: </label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        {/* Display subject field only when role is Teacher */}
        {role === "Teacher" && (
          <>
            <label>Subject: </label>
            <input
              type="text"
              value={subject}
              onChange={(e) => setSubject(e.target.value)}
              required={role === "Teacher"} // Make it required for Teacher
            />
          </>
        )}
        <label>Password: </label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <label>Role: </label>
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option value="Teacher">Teacher</option>
          <option value="Parent">Parent</option>
        </select>
        <button type="submit" disabled={loading}>
          {loading ? "Signing Up..." : "Sign Up"}
        </button>
        {signupSuccess && (
          <div className="success-message">
            <p>Signup successful! Redirecting to login...</p>
          </div>
        )}
      </form>
      <p>
        Already have an account? <Link to="/login">Login</Link>
      </p>
    </div>
  );
}

export default Signup;
