import React, { useEffect, useState } from "react";
import axios from "axios";

const TeacherDashboard = () => {
  const [students, setStudents] = useState([]);
  const [classes, setClasses] = useState([]);
  const [learningMaterials, setLearningMaterials] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [materialTitle, setMaterialTitle] = useState("");
  const [selectedStudentId, setSelectedStudentId] = useState(null);
  const [notificationMessage, setNotificationMessage] = useState("");
  const [sendToAll, setSendToAll] = useState(false);
  const [selectedClassId, setSelectedClassId] = useState(null);

  const token = localStorage.getItem("token");

  // Fetch all students or by class ID
  const getStudents = async (classId) => {
    try {
      const response = await axios.get("https://seclink-server.onrender.com/students", {
        headers: { Authorization: `Bearer ${token}` },
        params: { class_id: classId || "" },
      });
      setStudents(response.data);
    } catch (error) {
      console.error("Error fetching students:", error);
    }
  };

  // Fetch all classes
  const getClasses = async () => {
    try {
      const response = await axios.get("https://seclink-server.onrender.com/classes", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setClasses(response.data);
    } catch (error) {
      console.error("Error fetching classes:", error);
    }
  };

  // Fetch learning materials
  const getLearningMaterials = async () => {
    try {
      const response = await axios.get("https://seclink-server.onrender.com/learning-material", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setLearningMaterials(response.data);
    } catch (error) {
      console.error("Error fetching learning materials:", error);
    }
  };

  // Fetch notifications
  const getNotifications = async () => {
    try {
      const response = await axios.get("https://seclink-server.onrender.com/notifications", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setNotifications(response.data);
    } catch (error) {
      console.error("Error fetching notifications:", error);
    }
  };

  // Add Student
  const addStudent = async (studentData) => {
    try {
      await axios.post("https://seclink-server.onrender.com/students", studentData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      getStudents(); // Refresh the student list
    } catch (error) {
      console.error("Error adding student:", error);
    }
  };

  // Update Student Details
  const updateStudent = async (studentId, updatedData) => {
    try {
      await axios.put(`https://seclink-server.onrender.com/students/${studentId}`, updatedData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      getStudents(); // Refresh the student list
    } catch (error) {
      console.error("Error updating student:", error);
    }
  };

  // Delete Student
  const deleteStudent = async (studentId) => {
    try {
      await axios.delete(`https://seclink-server.onrender.com/students/${studentId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      getStudents(); // Refresh the student list
    } catch (error) {
      console.error("Error deleting student:", error);
    }
  };

  // Upload Learning Material
  const uploadLearningMaterial = async (event) => {
    event.preventDefault();
    if (!selectedFile || !materialTitle) {
      alert("Please fill in all fields.");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);
    formData.append("title", materialTitle);

    try {
      await axios.post("https://seclink-server.onrender.com/learning-material", formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
      });
      getLearningMaterials(); // Refresh learning materials
    } catch (error) {
      console.error("Error uploading learning material:", error);
    }
  };

  // Delete Learning Material
  const deleteLearningMaterial = async (materialId) => {
    try {
      await axios.delete(`https://seclink-server.onrender.com/learning-material/${materialId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      getLearningMaterials(); // Refresh learning materials
    } catch (error) {
      console.error("Error deleting learning material:", error);
    }
  };

  // Send Notification
  const sendNotification = async () => {
    const notificationData = {
      message: notificationMessage,
      send_to_all: sendToAll,
      class_id: sendToAll ? selectedClassId : null,
    };

    try {
      await axios.post("https://seclink-server.onrender.com/notifications", notificationData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      getNotifications(); // Refresh notifications
    } catch (error) {
      console.error("Error sending notification:", error);
    }
  };

  useEffect(() => {
    getStudents();
    getClasses();
    getLearningMaterials();
    getNotifications();
  }, []);

  return (
    <div>
      <h1>Teacher Dashboard</h1>

      {/* Students Section */}
      <div id="students">
        <h2>Students</h2>
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Class</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {students.map((student) => (
                <tr key={student.id}>
                  <td>{student.id}</td>
                  <td>{student.name}</td>
                  <td>{student.class}</td>
                  <td>
                    <button onClick={() => updateStudent(student.id, { /* Updated Data */ })}>Update</button>
                    <button onClick={() => deleteStudent(student.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <button onClick={() => addStudent({ name: "New Student", class_id: "1" })}>Add Student</button>
      </div>

      {/* Classes Section */}
      <div id="classes">
        <h2>Classes</h2>
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Teacher</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {classes.map((classItem) => (
                <tr key={classItem.id}>
                  <td>{classItem.id}</td>
                  <td>{classItem.name}</td>
                  <td>{classItem.teacher}</td>
                  <td>
                    <button onClick={() => deleteClass(classItem.id)}>Delete</button>
                    <button onClick={() => updateClass(classItem.id)}>Update</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Learning Materials Section */}
      <div id="learningMaterials">
        <h2>Learning Materials</h2>
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Title</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {learningMaterials.map((material) => (
                <tr key={material.id}>
                  <td>{material.id}</td>
                  <td>{material.title}</td>
                  <td>
                    <button onClick={() => deleteLearningMaterial(material.id)}>Delete</button>
                    <button onClick={() => updateLearningMaterial(material.id)}>Update</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <h3>Upload New Learning Material</h3>
        <form onSubmit={uploadLearningMaterial}>
          <label>
            Title:
            <input
              type="text"
              value={materialTitle}
              onChange={(e) => setMaterialTitle(e.target.value)}
              required
            />
          </label>
          <label>
            Select File:
            <input type="file" onChange={(e) => setSelectedFile(e.target.files[0])} required />
          </label>
          <button type="submit">Upload</button>
        </form>
      </div>

      {/* Notifications Section */}
      <div id="notifications">
        <h2>Notifications</h2>
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Message</th>
                <th>Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {notifications.map((notification) => (
                <tr key={notification.id}>
                  <td>{notification.id}</td>
                  <td>{notification.message}</td>
                  <td>{notification.date}</td>
                  <td>
                    <button onClick={() => updateNotification(notification.id)}>Update</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <h3>Send Notification</h3>
        <input
          type="text"
          value={notificationMessage}
          onChange={(e) => setNotificationMessage(e.target.value)}
          placeholder="Enter notification message"
        />
        <label>
          <input
            type="checkbox"
            checked={sendToAll}
            onChange={() => setSendToAll(!sendToAll)}
          />
          Send to all parents in the class
        </label>
        {sendToAll && (
          <select onChange={(e) => setSelectedClassId(e.target.value)} required>
            <option value="">Select Class</option>
            {classes.map((classItem) => (
              <option key={classItem.id} value={classItem.id}>{classItem.name}</option>
            ))}
          </select>
        )}
        <button onClick={sendNotification}>Send Notification</button>
      </div>
    </div>
  );
};

export default TeacherDashboard;
