import React, { useEffect, useState } from 'react';
import './TeacherDashboard.css'; // Import the CSS file for styling

const TeacherDashboard = () => {
  const [students, setStudents] = useState([]);
  const [classes, setClasses] = useState([]);
  const [learningMaterials, setLearningMaterials] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [file, setFile] = useState(null); // For file upload
  const [notificationMessage, setNotificationMessage] = useState(''); // Notification message input

  // Fetch all students or by class ID
  const getStudents = async (classId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5555/students?class_id=${classId || ''}`, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      if (response.ok) {
        setStudents(data);
      } else {
        console.error('Error fetching students:', data.message);
      }
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  // Fetch all classes
  const getClasses = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5555/classes', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      if (response.ok) {
        setClasses(data);
      } else {
        console.error('Error fetching classes:', data.message);
      }
    } catch (error) {
      console.error('Error fetching classes:', error);
    }
  };

  // Fetch learning materials
  const getLearningMaterials = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5555/learning-material', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      if (response.ok) {
        setLearningMaterials(data);
      } else {
        console.error('Error fetching learning materials:', data.message);
      }
    } catch (error) {
      console.error('Error fetching learning materials:', error);
    }
  };

  // Fetch notifications
  const getNotifications = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5555/notifications', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      if (response.ok) {
        setNotifications(data);
      } else {
        console.error('Error fetching notifications:', data.message);
      }
    } catch (error) {
      console.error('Error fetching notifications:', error);
    }
  };

  // Upload learning material with FormData for file upload
  const uploadLearningMaterial = async () => {
    if (!file) {
      alert('Please select a file to upload');
      return;
    }
    try {
      const token = localStorage.getItem('token');
      const formData = new FormData();
      formData.append('file', file); // 'file' should match the expected field in the backend
      formData.append('title', 'New Material'); // Optionally include other form fields

      const response = await fetch('http://localhost:5555/learning-material', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formData
      });
      const data = await response.json();
      if (response.ok) {
        console.log('Learning material uploaded successfully', data);
        getLearningMaterials(); // Refresh learning materials
      } else {
        console.error('Error uploading learning material:', data.message);
      }
    } catch (error) {
      console.error('Error uploading learning material:', error);
    }
  };

  // Add notification for parents
  const sendNotification = async (parentId = null) => {
    try {
      const token = localStorage.getItem('token');
      const notificationData = {
        message: notificationMessage,
        parent_id: parentId // Null for all parents
      };

      const response = await fetch('http://localhost:5555/notifications', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(notificationData)
      });
      const data = await response.json();
      if (response.ok) {
        console.log('Notification sent successfully', data);
        getNotifications(); // Refresh notifications
      } else {
        console.error('Error sending notification:', data.message);
      }
    } catch (error) {
      console.error('Error sending notification:', error);
    }
  };

  useEffect(() => {
    getStudents();
    getClasses();
    getLearningMaterials();
    getNotifications();
  }, []);

  return (
    <div>
      <h1>Teacher Dashboard</h1>

      <div id="learningMaterials">
        <h2>Learning Materials</h2>
        <input type="file" onChange={(e) => setFile(e.target.files[0])} />
        <button onClick={uploadLearningMaterial}>
          Upload Learning Material
        </button>
      </div>

      <div id="notifications">
        <h2>Send Notification</h2>
        <input
          type="text"
          placeholder="Enter message"
          value={notificationMessage}
          onChange={(e) => setNotificationMessage(e.target.value)}
        />
        <button onClick={() => sendNotification(null)}>
          Send to All Parents
        </button>
        <button onClick={() => sendNotification(1)}>
          Send to Specific Parent
        </button>
      </div>
    </div>
  );
};

export default TeacherDashboard;
