import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState(""); // For password reset
  const [resetMode, setResetMode] = useState(false); // To toggle between login and password reset
  const [loading, setLoading] = useState(false); // To track loading state
  const [errorMessage, setErrorMessage] = useState(""); // For inline error messages
  const navigate = useNavigate();

  // Handle Login Form Submission
  const handleLoginSubmit = (e) => {
    e.preventDefault();
    setLoading(true); // Show loading state
    setErrorMessage(""); // Reset error message

    // Make login request
    axios
      .post("http://localhost:5555/login", { username, password })
      .then((response) => {
        const token = response.data.token;
        localStorage.setItem("token", token);

        // Decode token to get the role
        const decodedToken = JSON.parse(atob(token.split('.')[1]));
        const role = decodedToken.role;

        // Redirect based on role
        if (role === "Teacher") {
          navigate("/teacher-dashboard");
        } else if (role === "Parent") {
          navigate("/parent-dashboard");
        }
        setLoading(false);
      })
      .catch((error) => {
        setLoading(false);
        setErrorMessage(error.response?.data?.error || "Login failed");
      });
  };

  // Handle Password Reset Form Submission
  const handlePasswordResetSubmit = (e) => {
    e.preventDefault();
    setLoading(true); // Show loading state
    setErrorMessage(""); // Reset error message

    // Make password reset request
    axios
      .post("http://localhost:5555/password-reset-request", { email })
      .then((response) => {
        setLoading(false);
        alert(response.data.message); // Notify the user
      })
      .catch((error) => {
        setLoading(false);
        setErrorMessage(
          error.response?.data?.message || "Error sending reset link"
        );
      });
  };

  return (
    <div className="card auth-card">
      {!resetMode ? (
        // Login form
        <form onSubmit={handleLoginSubmit}>
          <h2>Login</h2>
          {errorMessage && <p className="error-message">{errorMessage}</p>}
          <label>Username: </label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <label>Password: </label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit" disabled={loading}>
            {loading ? "Logging In..." : "Login"}
          </button>
          <p>
            Forgot your password?{" "}
            <Link to="#" onClick={() => setResetMode(true)}>
              Reset Password
            </Link>
          </p>
        </form>
      ) : (
        // Password Reset Form
        <form onSubmit={handlePasswordResetSubmit}>
          <h2>Reset Password</h2>
          {errorMessage && <p className="error-message">{errorMessage}</p>}
          <label>Email: </label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <button type="submit" disabled={loading}>
            {loading ? "Sending..." : "Send Reset Link"}
          </button>
          <p>
            Remember your password?{" "}
            <Link to="#" onClick={() => setResetMode(false)}>
              Login here
            </Link>
          </p>
        </form>
      )}
    </div>
  );
}

export default Login;
