import React, { useState } from "react";
import axios from "axios";
import {useNavigate } from "react-router-dom";

function Auth() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [resetMode, setResetMode] = useState(false); 
  const [username, setUsername] = useState(""); 
  const navigate = useNavigate();

  // Login function
  const handleLoginSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:5555/login", { username, password })
      .then((response) => {
        const token = response.data.token;
        localStorage.setItem("token", token); // Store the token in localStorage
        alert("Login successful!");
        navigate("/dashboard"); // Redirect to dashboard
      })
      .catch((error) => {
        alert(error.response?.data?.error || "Error logging in");
      });
  };

  // Password reset function
  const handlePasswordResetSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:5555/password-reset-request", { email })
      .then((response) => {
        alert(response.data.message);
      })
      .catch((error) => {
        alert(error.response?.data?.message || "Error sending reset link");
      });
  };

  return (
    <div className="card auth-card">
      {!resetMode ? (
        // Login form
        <form onSubmit={handleLoginSubmit}>
          <h2>Login</h2>
          <label>Username: </label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />

          <label>Password: </label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <button type="submit">Login</button>

          <p>
            Forgot your password?{" "}
            <span
              className="link"
              onClick={() => setResetMode(true)}
            >
              Reset Password
            </span>
          </p>
        </form>
      ) : (
        // Password Reset Form
        <form onSubmit={handlePasswordResetSubmit}>
          <h2>Reset Password</h2>
          <label>Email: </label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <button type="submit">Send Reset Link</button>

          <p>
            Remember your password?{" "}
            <span
              className="link"
              onClick={() => setResetMode(false)}
            >
              Login here
            </span>
          </p>
        </form>
      )}
    </div>
  );
}

export default Auth;
