import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";

const TeacherDashboard = () => {
  const [students, setStudents] = useState([]);
  const [classes, setClasses] = useState([]);
  const [learningMaterials, setLearningMaterials] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [materialTitle, setMaterialTitle] = useState("");
  const [notificationMessage, setNotificationMessage] = useState("");
  const [sendToAll, setSendToAll] = useState(false);
  const [selectedClassId, setSelectedClassId] = useState(null);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [studentDetails, setStudentDetails] = useState({});
  const [classDetails, setClassDetails] = useState({});
  const [selectedNotification, setSelectedNotification] = useState(null); // For updating notification
  const token = localStorage.getItem("token");

  // Fetch functions (getStudents, getClasses, getLearningMaterials, getNotifications)...

  // Add New Student
  const addStudent = async () => {
    // Implementation...
  };

  // Update Student Details
  const updateStudent = async () => {
    // Implementation...
  };

  // Delete Student
  const deleteStudent = async (studentId) => {
    // Implementation...
  };

  // Add New Class
  const addClass = async () => {
    // Implementation...
  };

  // Update Class
  const updateClass = async (classId) => {
    // Implementation...
  };

  // Delete Class
  const deleteClass = async (classId) => {
    // Implementation...
  };

  // Upload Learning Material
  const uploadLearningMaterial = async (event) => {
    // Implementation...
  };

  // Delete Learning Material
  const deleteLearningMaterial = async (materialId) => {
    // Implementation...
  };

  // Send Notification
  const sendNotification = async () => {
    // Implementation...
  };

  // Update Notification
  const updateNotification = async () => {
    if (!selectedNotification) return;

    const updatedData = {
      message: notificationMessage, // Use the message input
      // You can add more fields if necessary
    };

    try {
      await axios.put(`https://seclink-server.onrender.com/notifications/${selectedNotification.id}`, updatedData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      getNotifications(); // Refresh notifications
      setSelectedNotification(null); // Reset selection after update
      setNotificationMessage(""); // Clear message
    } catch (error) {
      console.error("Error updating notification:", error);
    }
  };

  useEffect(() => {
    getStudents();
    getClasses();
    getLearningMaterials();
    getNotifications();
  }, [token, getStudents, getClasses, getLearningMaterials, getNotifications]);

  return (
    <div>
      <h1>Teacher Dashboard</h1>

      {/* Students Section */}
      <div id="students">
        {/* Students table implementation... */}
      </div>

      {/* Classes Section */}
      <div id="classes">
        {/* Classes table implementation... */}
      </div>

      {/* Learning Materials Section */}
      <div id="learningMaterials">
        {/* Learning Materials table implementation... */}
      </div>

      {/* Notifications Section */}
      <div id="notifications">
        <h2>Notifications</h2>
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Message</th>
                <th>Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {notifications.map((notification) => (
                <tr key={notification.id}>
                  <td>{notification.id}</td>
                  <td>{notification.message}</td>
                  <td>{notification.date}</td>
                  <td>
                    <button onClick={() => {
                      setSelectedNotification(notification); // Select notification for update
                      setNotificationMessage(notification.message); // Populate message
                    }}>
                      Update
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <h3>Send Notification</h3>
        <input
          type="text"
          value={notificationMessage}
          onChange={(e) => setNotificationMessage(e.target.value)}
          placeholder="Enter notification message"
        />
        <label>
          <input
            type="checkbox"
            checked={sendToAll}
            onChange={() => setSendToAll(!sendToAll)}
          />
          Send to all parents in the class
        </label>
        {sendToAll && (
          <select onChange={(e) => setSelectedClassId(e.target.value)} required>
            <option value="">Select Class</option>
            {classes.map((classItem) => (
              <option key={classItem.id} value={classItem.id}>{classItem.name}</option>
            ))}
          </select>
        )}
        <button onClick={sendNotification}>Send Notification</button>

        {/* Update Notification Form */}
        {selectedNotification && (
          <div>
            <h3>Update Notification</h3>
            <form onSubmit={updateNotification}>
              <label>
                Message:
                <input
                  type="text"
                  value={notificationMessage}
                  onChange={(e) => setNotificationMessage(e.target.value)}
                  required
                />
              </label>
              <button type="submit">Update</button>
              <button type="button" onClick={() => setSelectedNotification(null)}>Cancel</button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default TeacherDashboard;
