import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [token, setToken] = useState(''); // State to hold the token
  const navigate = useNavigate(); // For redirecting after login

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5555/login', { username, password })
      .then((response) => {
        const loginToken = response.data.token;  // Get the token from response
        setToken(loginToken);
        localStorage.setItem('token', loginToken);  // Store token in localStorage

        alert('Login successful');
        navigate('/dashboard');  // Redirect to dashboard after login
      })
      .catch((error) => {
        alert(error.response.data.error || 'Error logging in');
      });
  };

  return (
    <div className="card login-card">
      <form onSubmit={handleSubmit}>
        <h2>Login</h2>
        <label>Username: </label>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />

        <label>Password: </label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />

        <button type="submit">Login</button>

        <p>
          Forgot Password? <Link to="/password-reset">Reset Password</Link>
        </p>
      </form>
    </div>
  );
}

export default Login;
