import React, { useEffect, useState } from "react";
import axios from "axios";

const TeacherDashboard = () => {
  const [students, setStudents] = useState([]);
  const [classes, setClasses] = useState([]);
  const [learningMaterials, setLearningMaterials] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [materialTitle, setMaterialTitle] = useState("");
  const [selectedStudentId, setSelectedStudentId] = useState(null); // For updates and deletions

  // Existing fetching functions...

  // Add Student
  const addStudent = async (studentData) => {
    // Implement the API call to add a student
  };

  // Update Student Details
  const updateStudent = async (studentId, updatedData) => {
    // Implement the API call to update student details
  };

  // Delete Student
  const deleteStudent = async (studentId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`https://seclink-server.onrender.com/students/${studentId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      getStudents(); // Refresh student list
    } catch (error) {
      console.error("Error deleting student:", error);
    }
  };

  // Existing functions for classes, learning materials, and notifications...

  // Delete Class
  const deleteClass = async (classId) => {
    // Implement API call to delete class
  };

  // Update Learning Material
  const updateLearningMaterial = async (materialId, updatedData) => {
    // Implement API call to update learning material
  };

  // Delete Learning Material
  const deleteLearningMaterial = async (materialId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`https://seclink-server.onrender.com/learning-material/${materialId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      getLearningMaterials(); // Refresh learning materials list
    } catch (error) {
      console.error("Error deleting learning material:", error);
    }
  };

  // Send Notification
  const sendNotification = async (notificationData) => {
    // Implement API call to send notification
  };

  useEffect(() => {
    getStudents();
    getClasses();
    getLearningMaterials();
    getNotifications();
  }, []);

  return (
    <div>
      <h1>Teacher Dashboard</h1>

      <div id="students">
        <h2>Students</h2>
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Class</th>
                <th>Actions</th> {/* New Actions Column */}
              </tr>
            </thead>
            <tbody>
              {students.map((student) => (
                <tr key={student.id}>
                  <td>{student.id}</td>
                  <td>{student.name}</td>
                  <td>{student.class}</td>
                  <td>
                    <button onClick={() => updateStudent(student.id)}>Update</button>
                    <button onClick={() => deleteStudent(student.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <button onClick={() => addStudent({ /* Student Data */ })}>Add Student</button>
      </div>

      <div id="classes">
        <h2>Classes</h2>
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Teacher</th>
                <th>Actions</th> {/* New Actions Column */}
              </tr>
            </thead>
            <tbody>
              {classes.map((classItem) => (
                <tr key={classItem.id}>
                  <td>{classItem.id}</td>
                  <td>{classItem.name}</td>
                  <td>{classItem.teacher}</td>
                  <td>
                    <button onClick={() => deleteClass(classItem.id)}>Delete</button>
                    <button onClick={() => updateClass(classItem.id)}>Update</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      <div id="learningMaterials">
        <h2>Learning Materials</h2>
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Title</th>
                <th>Actions</th> {/* New Actions Column */}
              </tr>
            </thead>
            <tbody>
              {learningMaterials.map((material) => (
                <tr key={material.id}>
                  <td>{material.id}</td>
                  <td>{material.title}</td>
                  <td>
                    <button onClick={() => deleteLearningMaterial(material.id)}>Delete</button>
                    <button onClick={() => updateLearningMaterial(material.id)}>Update</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <h3>Upload New Learning Material</h3>
        <form onSubmit={uploadLearningMaterial}>
          <label>
            Title:
            <input
              type="text"
              value={materialTitle}
              onChange={(e) => setMaterialTitle(e.target.value)}
              required
            />
          </label>
          <label>
            Select File:
            <input type="file" onChange={handleFileChange} required />
          </label>
          <button type="submit">Upload</button>
        </form>
      </div>

      <div id="notifications">
        <h2>Notifications</h2>
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Message</th>
                <th>Date</th>
                <th>Actions</th> {/* New Actions Column */}
              </tr>
            </thead>
            <tbody>
              {notifications.map((notification) => (
                <tr key={notification.id}>
                  <td>{notification.id}</td>
                  <td>{notification.message}</td>
                  <td>{notification.date}</td>
                  <td>
                    <button onClick={() => sendNotification({ /* Notification Data */ })}>Send Notification</button>
                    <button onClick={() => updateNotification(notification.id)}>Update</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default TeacherDashboard;
