import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

function Auth() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("teacher"); // Default role can be 'teacher' or 'parent'
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  // Login function
  const handleLoginSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    setErrorMessage("");

    axios
      .post("http://localhost:5555/login", { username, password, role }) // Ensure role is sent
      .then((response) => {
        const token = response.data.token;
        localStorage.setItem("token", token); // Store the token in localStorage
        setLoading(false);
        navigate("/dashboard"); // Redirect to dashboard
      })
      .catch((error) => {
        setLoading(false);
        setErrorMessage(error.response?.data?.error || "Error logging in");
      });
  };

  return (
    <div className="card auth-card">
      <form onSubmit={handleLoginSubmit}>
        <h2>Login</h2>
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        <label>Username: </label>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <label>Password: </label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <label>Role: </label>
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option value="teacher">Teacher</option>
          <option value="parent">Parent</option>
        </select>
        <button type="submit" disabled={loading}>
          {loading ? "Logging In..." : "Login"}
        </button>
      </form>
      <p>
        Don't have an account? <Link to="/signup">Sign up</Link>
      </p>
    </div>
  );
}

export default Auth;
